mapper.select:
select 

from CRM.FIN_OPB_FEEDETAIL
where RECIPE_NO = #{recipeNo,jdbcType=VARCHAR}
  and SEQUENCE_NO = #{sequenceNo,jdbcType=NUMERIC}
  and TRANS_TYPE = #{transType,jdbcType=VARCHAR}
  and MO_ORDER = #{moOrder,jdbcType=VARCHAR}
  and INVOICE_SEQ = #{invoiceSeq,jdbcType=VARCHAR}
  and HOSPITAL_ID = #{hospitalId,jdbcType=VARCHAR}

mapper.delete:
delete from CRM.FIN_OPB_FEEDETAIL
where RECIPE_NO = #{recipeNo,jdbcType=VARCHAR}
  and SEQUENCE_NO = #{sequenceNo,jdbcType=NUMERIC}
  and TRANS_TYPE = #{transType,jdbcType=VARCHAR}
  and MO_ORDER = #{moOrder,jdbcType=VARCHAR}
  and INVOICE_SEQ = #{invoiceSeq,jdbcType=VARCHAR}
  and HOSPITAL_ID = #{hospitalId,jdbcType=VARCHAR}
