mapper.select:
select 

from CRM.FIN_OPR_REGISTER
where CLINIC_CODE = #{clinicCode,jdbcType=VARCHAR}
  and TRANS_TYPE = #{transType,jdbcType=VARCHAR}
  and HOSPITAL_ID = #{hospitalId,jdbcType=VARCHAR}

mapper.delete:
delete from CRM.FIN_OPR_REGISTER
where CLINIC_CODE = #{clinicCode,jdbcType=VARCHAR}
  and TRANS_TYPE = #{transType,jdbcType=VARCHAR}
  and HOSPITAL_ID = #{hospitalId,jdbcType=VARCHAR}
